package com.example.t5a1_vernich_adrian

import android.content.Intent
import android.os.Bundle
import androidx.appcompat.app.AppCompatActivity
import androidx.recyclerview.widget.DividerItemDecoration
import androidx.recyclerview.widget.LinearLayoutManager
import com.example.t5a1_vernich_adrian.databinding.ActivityMainBinding

class MainActivity : AppCompatActivity(), OnClickListener {
    private lateinit var comidaAdapter: ComidaAdapter
    private lateinit var linearLayoutManager: LinearLayoutManager
    private lateinit var itemDecoration: DividerItemDecoration

    private lateinit var binding: ActivityMainBinding

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        binding = ActivityMainBinding.inflate((layoutInflater))
        setContentView(binding.root)

        comidaAdapter = ComidaAdapter(getUsers(), this)
        linearLayoutManager = LinearLayoutManager(this)
        itemDecoration = DividerItemDecoration(this, DividerItemDecoration.VERTICAL)

        binding.recyclerview.apply {
            layoutManager = linearLayoutManager
            adapter = comidaAdapter
            addItemDecoration(itemDecoration)
        }
    }

    private fun getUsers(): MutableList<Comida> {
        val comidas = mutableListOf<Comida>()

        val pizza  = Comida ("Pizza", "data:image/jpeg;base64,", "https://es.wikipedia.org/wiki/Pizza")
        val paella  = Comida ( "Paella", "https://th.bing.com/th/id/OIP.M2YcLP3xZAcno7ybhohuwgAAAA?w=166&h=125&c=7&r=0&o=5&dpr=1.3&pid=1.7", "https://es.wikipedia.org/wiki/Paella")
        val kebap  = Comida ( "Kebap", "https://th.bing.com/th/id/OIP.JGx3IWG6KiZNrYzk7HXNXgAAAA?w=158&h=122&c=7&r=0&o=5&dpr=1.3&pid=1.7", "https://es.wikipedia.org/wiki/Kebab")
        val hamburguesa  = Comida ( "Hamburguesa", "https://th.bing.com/th/id/OIP.5BC089Qmw0RKrm8qGaS1-QAAAA?w=282&h=187&c=7&r=0&o=5&dpr=1.3&pid=1.7", "https://es.wikipedia.org/wiki/Hamburguesa")
        val pasta  = Comida ( "Pasta", "https://th.bing.com/th/id/OIP.45QSQAAWrBMGKCpZcITMbQHaEk?w=300&h=185&c=7&r=0&o=5&dpr=1.3&pid=1.7", "https://es.wikipedia.org/wiki/Pasta")

        comidas.add(pizza)
        comidas.add(paella)
        comidas.add(kebap)
        comidas.add(hamburguesa)
        comidas.add(pasta)

        return comidas
    }

    override fun onClick(comida: Comida) {
        val intent = Intent(this, webView::class.java)
        intent.putExtra("wiki", comida.wiki)
        startActivity(intent)
    }
}